//import logo from './logo.svg';
import React from "react";
import './App.css';
import { Container, Row, Form, FormGroup, FormControl, FormLabel, Button, Alert, Table } from "react-bootstrap";
import axios from 'axios';

class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
	libelle:"",
  description:"",
  lieu:"",
  date:"",
  heure:"",
  cible:"" };
	}
  // Changer contenus des champs 
  handleChange= evt =>{
    this.setState({[evt.target.name]:evt.target.value});
  };


  submitReview =() =>{
  axios.post("http://localhost:3001/");
  };

  render()
  {
    return (
    <div>

      <Container>
      <Row>
						<Form>
							<FormGroup>
								<FormLabel>Nom</FormLabel>
								<FormControl type="text" name="libelle" placeholder="Intitulé de l'évenement" onChange={this.handleChange} value={this.state.libelle}></FormControl>
							</FormGroup>
							<FormGroup>
								<FormLabel>Description</FormLabel>
                <textarea class="form-control" name="description" rows="3" value={this.state.description} onChange={this.handleChange} placeholder="Plus d'informations sur l'évenement"/>
              </FormGroup>
              <FormGroup>
								<FormLabel>Lieu</FormLabel>
								<FormControl type="text" name="lieu" value={this.state.lieu} onChange={this.handleChange} placeholder="Ajouter l'adresse"></FormControl>
							</FormGroup>
              <FormGroup>
								<FormLabel>Date</FormLabel>
                <input class="form-control" type="date"  name="date" value={this.state.date} onChange={this.handleChange} />
            	</FormGroup>
              <FormGroup>
								<FormLabel>Heure</FormLabel>
                <input class="form-control" type="time" name="heure" value={this.state.heure} onChange={this.handleChange} />
              </FormGroup>
							<Button onClick={submitReview} >Ajouter</Button>
						</Form>
					</Row>
      </Container>
    </div>
    )
  }
}
export default App;
