import React from "react";
import { Container, Row, Form, FormGroup, FormControl, FormLabel, Button } from "react-bootstrap";
import Axios from 'axios'
import {useState} from 'react'

    function App(){

      const [nom,setNom]=useState("");
      const [description,setdescription]=useState("");

      const [eventList,setEventList]=useState([]);

      //recuperation des evenemnt
      const getEvent =()=>{
        Axios.get('http://localhost:3001/event').then ((response)=>{
          setEventList(response.data);
        });
      };

      //ajout d'une evenement
      const ajoutEvent=()=>{
        Axios.post('http://localhost:3001/ajoutEvent',{
          nom:nom,
          description:description
        }).then(()=>{
          setEventList([
           ...eventList,
           {
            nom:nom,
            description:description
           }
          ])

        })

      }

      /*mise a jour des données
      const modiferEvent = (id) => {
        Axios.put("http://localhost:3001/modifierEvent", { nom: nom, description: description }).then(
          (response) => {
            setEventList(
              eventList.map((val) => {
                return val.id == id
                  ? {
                      nom: val.nom,
                      description: val.description,
                    }
                  : val;
              })
            );
          }
        );
      };*/


    return (
    <div>

      <Container>
      <Row>
						<Form>
							<FormGroup>
								<FormLabel>Nom</FormLabel>
								<FormControl type="text" name="nom" placeholder="Intitulé de l'évenement" onChange={(event)=>{setNom(event.target.value)}} ></FormControl>
							</FormGroup>
							<FormGroup>
								<FormLabel>Description</FormLabel>
                <textarea class="form-control" name="description" rows="3"  placeholder="Plus d'informations sur l'évenement"  onChange={(event)=>{setdescription(event.target.value)}}/>
              </FormGroup>
              <FormGroup>
								<FormLabel>Lieu</FormLabel>
								<FormControl type="text" name="lieu"  placeholder="Ajouter l'adresse"></FormControl>
							</FormGroup>
              <FormGroup>
								<FormLabel>Date</FormLabel>
                <input class="form-control" type="date"  name="date"  />
            	</FormGroup>
              <FormGroup>
								<FormLabel>Heure</FormLabel>
                <input class="form-control" type="time" name="heure" />
              </FormGroup>
							<Button onClick={ajoutEvent}>Ajouter</Button>

						</Form>

            <Button onClick={getEvent}>show</Button>
              {eventList.map((val,key)=>{
                return(
                  <div>
                    <p>Nom:{val.nom}</p>
                    <p>Nom:{val.description}</p>
                    <Button onClick={modiferEvent(val.id)}>Modifer</Button>
                  </div>
                )
              })}
					</Row>
      </Container>
    </div>
    )
  }

export default App;
