import React from "react";
import { Container, Row, Form, FormGroup, FormControl, FormLabel, Button } from "react-bootstrap";
import Axios from 'axios'
import {useState} from 'react'





    function App(){

      const [nom,setNom]=useState("");
      const [description,setdescription]=useState("");
      const [lieu,setlieu]=useState("");
      const [date,setdate]=useState("");
      const [heure,setheure]=useState("");

      const [eventList,setEventList]=useState([]);

      //recuperation des evenemnt
      const getEvent =()=>{
        Axios.get('http://localhost:3001/event').then ((response)=>{
          setEventList(response.data);
        });
      };



      //ajout d'une evenement
      const ajoutEvent=()=>{
        Axios.post('http://localhost:3001/ajoutEvent',{
          nom:nom,
          description:description,
          lieu:lieu,
          date:date,
          heure:heure,
        }).then(()=>{
          setEventList([
           ...eventList,
           {
            nom:nom,
            description:description,
            lieu:lieu,
            date:date,
            heure:heure,
           }
          ])

        })

      }

      const supprimeEvent = (nom) => {
        Axios.delete(`http://localhost:3001/supprimeEvent/${nom}`).then((response) => {
          setEventList(
            eventList.filter((val) => {
              return val.nom != nom;
            })
          );
        });
      };



