        setEvent(result.data);
        console.log(result.nom);
      };


      const sendUpdate = (nom) => {
        Axios.put(`http://localhost:3001/update/${nom}`).then(
          (response) => {
            setEvent(
              event.map((val) => {
                return val.nom == nom
