    return (
    <div>
      <Container>
        <div class="row"> 
        <div class="col s12">
          <h3>CrÃ©ation</h3>
          <h6>EvÃ©nement</h6>
          <hr></hr>
                    <Form class="form-group">


                                 <FormGroup>
                                  <FormLabel>Nom</FormLabel>
                                  <FormControl  type="text" name="nom" placeholder="IntitulÃ© de l'Ã©venement" onChange={(event)=>{setNom(event.target.value)}} ></FormControl>
                                </FormGroup>


                                <FormGroup>
                                  <FormLabel>Description</FormLabel>
                                  <textarea class="form-control" name="description" rows="3"  placeholder="Plus d'informations sur l'Ã©venement"  onChange={(event)=>{setdescription(event.target.value)}}/>
                                </FormGroup>

                                <div class="form-row">
                               <div class="col-6"> 
                                <FormGroup>
                                  <FormLabel>Lieu</FormLabel>
                                  <FormControl type="text" name="lieu"  placeholder="Ajouter l'adresse" onChange={(event)=>{setlieu(event.target.value)}}></FormControl>
                                </FormGroup>
                                </div>
                                <div class="col-3"> 
                                <FormGroup >
                                  <FormLabel>Date</FormLabel>
                                  <input class="form-control" type="date"  name="date"  onChange={(event)=>{setdate(event.target.value)}}/>
                                </FormGroup>
                                </div>
                                <div class="col-3"> 
                                <FormGroup>
                                  <FormLabel>Heure</FormLabel>
                                  <input class="form-control" type="time" name="heure" onChange={(event)=>{setheure(event.target.value)}} />
                                </FormGroup>
                                </div>
                              </div>
                                <Button class="btn btn-success btn-block"  onClick={ajoutEvent}>Ajouter</Button>

                                <hr></hr>
                              </Form>    

              </div>
              </div>

                  <div class="row">

                  <h6>EvÃ©nement</h6>

          <table class="table border shadow">
          <thead class="thead-dark">
            <tr>
              <th scope="col">#</th>
              <th scope="col">Nom</th>
              <th scope="col">Description</th>
              <th scope="col">Lieu</th>
              <th scope="col">Date</th>
              <th scope="col">Heure</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
          {eventList.map((evt, index) => (
              <tr>
                <th scope="row">{index + 1}</th>
                <td>{evt.nom}</td>
                <td>{evt.description}</td>
                <td>{evt.lieu}</td>
                <td>{evt.date}</td>
                <td>{evt.heure}</td>

                <td>
                  <button class="btn btn-danger" onClick={() => {supprimeEvent(evt.nom)}}> Supprimer</button>
