                      nom: val.nom,
                      description: val.description,
                    }
                  : val;
              })
            );
          }
        );
      };*/


    return (
    <div>
      <Container>
        <div class="row">
            <div class="col-8">
            <Button onClick={getEvent}>show</Button>

            <h1>Evénements</h1>
        <table class="table border shadow">
          <thead class="thead-dark">
            <tr>
              <th scope="col">#</th>
              <th scope="col">Nom</th>
              <th scope="col">Description</th>
              <th scope="col">Email</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
          {eventList.map((evt, index) => (
              <tr>
                <th scope="row">{index + 1}</th>
                <td>{evt.nom}</td>
                <td>{evt.description}</td>
                <td></td>
                <td>
                  <a href="/GestionEvent" class="btn btn-primary mr-2"  >View</a>

                  <button class="btn btn-danger" onClick={() => {supprimeEvent(evt.nom)}}> Supprimer</button>


                  <a href="/GestionEvent" class="btn btn-outline-primary mr-2" >Modifier</a>
                </td>
              </tr>
            ))}
          </tbody>
        </table>





            </div>
            <div class="col-4" >
            <Form>
                        <FormGroup>
                          <FormLabel>Nom</FormLabel>
                          <FormControl type="text" name="nom" placeholder="Intitulé de l'évenement" onChange={(event)=>{setNom(event.target.value)}} ></FormControl>
                        </FormGroup>
                        <FormGroup>
                          <FormLabel>Description</FormLabel>
                          <textarea class="form-control" name="description" rows="3"  placeholder="Plus d'informations sur l'évenement"  onChange={(event)=>{setdescription(event.target.value)}}/>
                        </FormGroup>
                        <FormGroup>
                          <FormLabel>Lieu</FormLabel>
                          <FormControl type="text" name="lieu"  placeholder="Ajouter l'adresse"></FormControl>
                        </FormGroup>
                        <FormGroup>
                          <FormLabel>Date</FormLabel>
                          <input class="form-control" type="date"  name="date"  />
                        </FormGroup>
                        <FormGroup>
                          <FormLabel>Heure</FormLabel>
                          <input class="form-control" type="time" name="heure" />
                        </FormGroup>
                        <Button class="btn btn-success btn-block" onClick={ajoutEvent}>Ajouter</Button>
                      </Form>
            </div>
      </div>

            </Container>
    </div>
    )
  }

export default App;
