//import logo from './logo.svg';
import React from "react";
import './App.css';
import { Container, Row, Form, FormGroup, FormControl, FormLabel, Button } from "react-bootstrap";
import axios from 'axios';
import {useState} from 'react'

/*
class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
	libelle:"",
  description:"",
  lieu:"",
  date:"",
  heure:"",
  cible:"" };
	}
  // Changer contenus des champs 
  handleChange= evt =>{
    this.setState({[evt.target.name]:evt.target.value});
  };


  submitReview =() =>{
  axios.post("http://localhost:3001/api/insert");
  };

  render()
  {*/

    function App(){
      const [eventList,setEventList]=useState([]);
      const getEvent =()=>{
        axios.get('http://localhost:3001/event').then ((Response)=>{
          setEventList(Response.data);
        });
      }
    return (
    <div>

      <Container>
      <Row>
						<Form>
							<FormGroup>
								<FormLabel>Nom</FormLabel>
								<FormControl type="text" name="libelle" placeholder="Intitulé de l'évenement" ></FormControl>
							</FormGroup>
							<FormGroup>
								<FormLabel>Description</FormLabel>
                <textarea class="form-control" name="description" rows="3"  placeholder="Plus d'informations sur l'évenement"/>
              </FormGroup>
              <FormGroup>
								<FormLabel>Lieu</FormLabel>
								<FormControl type="text" name="lieu"  placeholder="Ajouter l'adresse"></FormControl>
							</FormGroup>
              <FormGroup>
								<FormLabel>Date</FormLabel>
                <input class="form-control" type="date"  name="date"  />
            	</FormGroup>
              <FormGroup>
								<FormLabel>Heure</FormLabel>
                <input class="form-control" type="time" name="heure" value={this.state.heure} onChange={this.handleChange} />
              </FormGroup>
							<Button>Ajouter</Button>
              <Button onClick={getEvent}>show</Button>
              {eventList.map((val,key)=>{
                return
                (
                  <div>
                    <p>Nom:{val.nom}</p>
                    <p>Nom:{val.description}</p>
                  </div>
                )
              })}
						</Form>
					</Row>
      </Container>
    </div>
    )
  }

export default App;
